    @Encoding   NVarChar(10), 
    @Homepage   NVarChar(100),
    @FileName	NVarChar(255),
    @FileSize	Int,    
    @Id Int
As

-- 게시판 아티클에 답변 저장 
CREATE PROCEDURE [dbo].[AnswersReply]
    @Name       NVarChar(25), 
